import React, { useRef, useEffect, useState } from 'react';
import '../../App.css';
import Footer from '../Footer'
import CardItem from './CardItem';
import '../Footer.css';
import ReactImageGallery from '../ReactImageGallery';

export default function Chambres() {
  const chambresRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);

  function nextSlide() {
    setCurrentSlide((currentSlide + 1) % images.length);
  }

  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 2000);
    return () => clearInterval(interval);
  }, [currentSlide]);

  const images = [
    'images/bar3.jpg',
    'images/restau2.jpg',
    'images/bar2.jpg',
    'images/bar1.jpg',
    
    
  ];

  return (
    <div> <h1 ref={chambresRef} className='hotel '>
      <div className='slide active' style={{ backgroundImage: `url(${images[currentSlide]})` }}></div>
      <div className='slide ' style={{ backgroundImage: `url(${images[(currentSlide + 1) % images.length]})` }}></div>
      <div className='slide' style={{ backgroundImage: `url(${images[(currentSlide + 2) % images.length]})` }}></div>
      
    </h1>
      
      
      <div className='cards'>
        <h1 className='classification'>Notre Bar </h1>
        <div className='cards__container'>
          <div className='cards__wrapper'>
            <ul className='cards__items'>
              <CardItem
                src='images/restau.jpg'
                text='description restaurant.'
                label='Restaurant '

              />
              <CardItem
                src='images/plat.jpg'
                text='description du plat.'
                label='Plat préferé'

              />
              <CardItem
                src='images/plat.jpg'
                text='description du plat.'
                label='Plat préferé'

              />
            </ul>
            <ul className='cards__items'>
            
              
              
            </ul>
          </div>
        </div>
      </div>
      <ReactImageGallery/>
      <Footer/>
    </div>

  );
}
