import React, { useRef, useEffect, useState } from 'react';
import '../../App.css';
import Footer from '../Footer'
import CardItem from './CardItem';

export default function Chambres() {
  const chambresRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);

  function nextSlide() {
    setCurrentSlide((currentSlide + 1) % images.length);
  }

  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 5000);
    return () => clearInterval(interval);
  }, [currentSlide]);

  const images = [
    'images/restau.jpg',
    'images/rooftop.jpg',
    'images/bar.jpg',
  ];

  return (
    <div> <h1 ref={chambresRef} className='hotel '>
      <div className='slide active' style={{ backgroundImage: `url(${images[currentSlide]})` }}></div>
      <div className='slide ' style={{ backgroundImage: `url(${images[(currentSlide + 1) % images.length]})` }}></div>
      <div className='slide' style={{ backgroundImage: `url(${images[(currentSlide + 2) % images.length]})` }}></div>
      Restaurant
    </h1>
    
      <div className='cards'>
      <h1 className='classification'>Notre Restaurant </h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            <CardItem
              src='images/restau.jpg'
              text='description restaurant.'
              label='Restaurant '
             
            />
            <CardItem
              src='images/plat.jpg'
              text='Suites de 38 m2 Chambre à lit queen size.
               Décor élégant et classique aux riches tentures.
                Salle de séjour séparée, dotée dun canapé et de fauteuils. 
                Vue sur la ville dEstoril. Salle de bains en marbre,
                 dont certaines avec baignoire et douche séparées.'
              label='Plat préferé'

            />
          </ul>
          <ul className='cards__items'>
            <CardItem
              src='images/chambredou.jpg'
              text='Chambres situées aux étages supérieurs de lhôtel,
               avec balcon et vue imprenable sur le jardin de lhôtel,
                le parc dEstoril et la baie de Cascais. Décor élégant et luxueux,
                 aux détails contemporains, très accueillant. Les chambres offrent 
                 une sensation de confort intemporel créée pour des moments spéciaux 
                 et un séjour paisible. Lits simples ou lits doubles, queen ou king size.
                  Chambres de 25 à 35 m2. Salles de bains en marbre à baignoire et douche séparées.'
              label='Chambre Double'

            />
            <CardItem
              src='images/rooftop.jpg'
              text='Suites de 38 à 43 m2. Chambre à lit queen ou king size, de facture raffinée.
               Décor élégant et luxueux aux riches tentures. Salle de séjour séparée,
                dotée dun canapé et de fauteuils. La plupart des suites possède un balcon et
                 une merveilleuse vue sur les jardins de lhôtel et Estoril (vue sur la mer aux 
                  étages supérieurs). Salle de bains en marbre, dont certaines avec baignoire et douche séparées.'
              label='Chambre quadriple'

            />
          </ul>
        </div>
      </div>
    </div>
    </div>

  );
}
