import React, { useRef, useEffect, useState } from 'react';
import '../../App.css';
import Footer from '../Footer'
import CardItem from './CardItem';
import '../Footer.css';

export default function Chambres() {
  const chambresRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);

  function nextSlide() {
    setCurrentSlide((currentSlide + 1) % images.length);
  }

  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 5000);
    return () => clearInterval(interval);
  }, [currentSlide]);

  const images = [
    'images/bar3.jpg',
    'images/bar2.jpg',
    'images/bar1.jpg',
  ];

  return (
    <div> <h1 ref={chambresRef} className='hotel '>
      <div className='slide active' style={{ backgroundImage: `url(${images[currentSlide]})` }}></div>
      <div className='slide ' style={{ backgroundImage: `url(${images[(currentSlide + 1) % images.length]})` }}></div>
      <div className='slide' style={{ backgroundImage: `url(${images[(currentSlide + 2) % images.length]})` }}></div>
      Restaurant
    </h1>
      
      <h1 className='resto'>Notre Restaurant</h1>
      
      <div className='img_resto'>
        <div className='img_container' style={{ marginRight: '10px' }}>
          <img src={`${process.env.PUBLIC_URL}/images/restau.jpg`} alt='Restaurant 1' />
        </div>
        <div className='img_container' style={{ marginLeft: '10px' }}>
          <img src={`${process.env.PUBLIC_URL}/images/restau.jpg`} alt='Restaurant 2' />
        </div>
      </div>
      <div className='cards'>
        <h1 className='classification'>Notre Restaurant </h1>
        <div className='cards__container'>
          <div className='cards__wrapper'>
            <ul className='cards__items'>
              <CardItem
                src='images/restau.jpg'
                text='description restaurant.'
                label='Restaurant '

              />
              <CardItem
                src='images/plat.jpg'
                text='description du plat.'
                label='Plat préferé'

              />
              <CardItem
                src='images/plat.jpg'
                text='description du plat.'
                label='Plat préferé'

              />
            </ul>
            <ul className='cards__items'>
            
              
              
            </ul>
          </div>
        </div>
      </div>
      <Footer/>
    </div>

  );
}
