import React, { useRef, useEffect, useState } from 'react';
import '../../App.css';

export default function Chambres() {
  const chambresRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);

  function nextSlide() {
    setCurrentSlide((currentSlide + 1) % images.length);
  }

  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 5000);
    return () => clearInterval(interval);
  }, [currentSlide]);

  const images = [
    'images/chambre1.jpg',
    'images/dondon.jpg',
    'images/bar.jpg',
  ];

  return (
    <h1 ref={chambresRef} className='chambres slideshow'>
      <div className='slide active' style={{ backgroundImage: `url(${images[currentSlide]})` }}></div>
      <div className='slide' style={{ backgroundImage: `url(${images[(currentSlide + 1) % images.length]})` }}><h1 class="chambres-title">Nos Chambres</h1></div>
      <div className='slide' style={{ backgroundImage: `url(${images[(currentSlide + 2) % images.length]})` }}></div>
      Nos Chambres
    </h1>
  );
}
