import React, { useState } from 'react';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';

const images = [
  'images/bar6.jpg',
  'images/bar1.jpg',
  'images/bar3.jpg',
  'images/bar4.jpg',
  'images/bar5.jpg',
  'images/bar2.jpg',
];

function ReactImageGallery() {
  const [data, setData] = useState({ img: '', i: 0 });

  const viewImage = (img, i) => {
    setData({ img, i });
  };

  const imgAction = (action) => {
    let i = data.i;
    if (action === 'next-img') {
      setData({ img: images[i + 1], i: i + 1 });
    }
    if (action === 'previous-img') {
      setData({ img: images[i - 1], i: i - 1 });
    }
  };

  return (
    <>
      {data.img && (
        <div
          style={{
            width: '100%',
            height: '100vh',
            background: 'black',
            position: 'fixed',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            overflow: 'hidden',
          }}
        >
          <button style={{ position: 'absolute', top: '10px', right: '10px' }}>X</button>
          <button onClick={() => imgAction('previous-img')}>Previous</button>
          <img src={data.img} style={{ width: 'auto', maxWidth: '90%', maxHeight: '90%' }} alt='' />
          <button onClick={() => imgAction('next-img')}>Next</button>
        </div>
      )}
      <div style={{ padding: '10px' }}>
        <ResponsiveMasonry columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 3 }}>
          <Masonry gutter='20px'>
            {images.map((image, i) => (
              <div key={i} onClick={() => viewImage(image, i)} style={{ cursor: 'pointer' }}>
                <img src={image} style={{ width: '100%', display: 'block' }} alt='' />
              </div>
            ))}
          </Masonry>
        </ResponsiveMasonry>
      </div>
    </>
  );
}

export default ReactImageGallery;
