import React from 'react';
import '../../App.css';
import '../Apropos.css';
import Footer from '../Footer';

export default function Apropos() {
  return (
    <div className="apropos-container">
      <div className="apropos-image">
        <img src="images/apropos.jpg" alt="Ravi de vous accueillir" />
        <div className="apropos-text">
          <h2>Ravi de vous accueillir</h2>
          <p>Vous trouverez ci-dessous les informations nécessaires à propos de nous.</p>
        </div>
      </div>
      <div className='about-info'>
  <p className='address'>Adresse : Route de l'aéroport km 0.5, Sfax, Tunisie</p>
  <p className='phone'>Téléphone : 70 253 565</p>
  <p className='email'>E-mail : hoteldonia@hoteldonia.com.tn</p>
</div>

      <Footer/>
    </div>
  );
}
